import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ProductList from './components/ProductList';
import ProductDetail from './components/ProductDetail';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Confirmation from './components/Confirmation';
import Header from './components/Header';
import Login from './components/Login';
import SignUp from './components/SignUp';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import OrderList from './components/OrderList';
import OrderDetails from './components/OrderDetails';
import AccountPage from './components/AccountPage';
import { CartProvider } from './context/CartContext';
import './App.css';

function isAdminAuthenticated() {
  const token = localStorage.getItem('token');
  const isAdmin = localStorage.getItem('isAdmin') === 'true'; // Check if the user is an admin
  return token && isAdmin; // Return true if the user is authenticated and is an admin
}

function App() {
  return (
    <CartProvider>
      <Router>
        <Header />
        <div className="app-container">
          <Routes>
            <Route path="/" element={<ProductList />} />
            <Route path="/product/:id" element={<ProductDetail />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/checkout" element={<Checkout />} />
            <Route path="/confirmation" element={<Confirmation />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<SignUp />} />
            <Route path="/admin/login" element={<AdminLogin />} />
            <Route
              path="/admin/dashboard"
              element={
                isAdminAuthenticated() ? <AdminDashboard /> : <Navigate to="/admin/login" />
              }
            />
            <Route path="/orders" element={<OrderList userId={localStorage.getItem('userId')} />} />
            <Route path="/order/:orderId" element={<OrderDetails />} />
            <Route path="/account" element={<AccountPage />} />
          </Routes>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
