import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AdminOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token'); // Get the token from localStorage
    const isAdminValue = localStorage.getItem('isAdmin') === 'true'; // Parse isAdmin as boolean

    if (!token) {
      navigate('/login'); // Redirect to login if no token is found
      return;
    }

    if (!isAdminValue) {
      navigate('/'); // Redirect to homepage if not an admin
      return;
    }

    // Fetch orders from the backend
    fetch('http://localhost:5002/api/admin/orders', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`, // Send the token in the Authorization header
      },
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else if (response.status === 403) {
          navigate('/'); // Redirect if not admin
        } else {
          throw new Error('Failed to fetch orders.');
        }
      })
      .then((data) => {
        setOrders(data); // Store the orders in state
      })
      .catch((err) => {
        console.error('Error fetching orders:', err);
        setError(err.message); // Handle any errors
      })
      .finally(() => {
        setLoading(false); // Set loading to false when the request is complete
      });
  }, [navigate]);

  if (loading) {
    return <div>Loading...</div>; // Show loading spinner while fetching
  }

  if (error) {
    return <div>Error: {error}</div>; // Show error if something goes wrong
  }

  return (
    <div>
      <h1>Admin Orders</h1>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>User</th>
              <th>Total Amount</th>
              <th>Status</th>
              <th>Created At</th>
              <th>Shipping Address</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id}>
                <td>{order._id}</td>
                <td>{order.user?.username || 'Unknown'}</td>
                <td>${order.totalAmount}</td>
                <td>{order.status}</td>
                <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                <td>{order.shippingAddress}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AdminOrders;
