import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function AdminDashboard() {
  const navigate = useNavigate();
  const [isAdmin, setIsAdmin] = useState(false); // State for admin status
  const [loading, setLoading] = useState(true);  // State for loading

  useEffect(() => {
    const token = localStorage.getItem('token'); // Get the token from localStorage
    const isAdminValue = localStorage.getItem('isAdmin') === 'true'; // Parse isAdmin as boolean

    if (!token) {
      navigate('/login'); // Redirect to login if no token is found
      return;
    }

    if (!isAdminValue) {
      navigate('/'); // Redirect to homepage if not an admin
      return;
    }

    // Check if the user is still an admin by making a backend request
    fetch('http://localhost:5002/api/admin/check', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`, // Send the token in the Authorization header
      },
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else if (response.status === 403) {
          navigate('/'); // Redirect non-admins to the homepage
        } else {
          throw new Error('Failed to verify admin status.');
        }
      })
      .then(data => {
        if (data.message === 'You are an admin') {
          setIsAdmin(true); // Grant access to the admin dashboard
        } else {
          navigate('/'); // Redirect non-admins to the homepage
        }
      })
      .catch(() => {
        navigate('/login'); // Redirect to login on error
      })
      .finally(() => {
        setLoading(false); // Stop loading after the check
      });
  }, [navigate]);

  if (loading) {
    return <div>Loading...</div>; // Show a loading state while checking admin status
  }

  if (!isAdmin) {
    return <div>Access denied. Redirecting...</div>; // Optional message before redirection
  }

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <nav>
        <Link to="/admin/orders">Manage Orders</Link>
        <br />
        <Link to="/admin/reviews">Manage Reviews</Link>
      </nav>
    </div>
  );
}

export default AdminDashboard;
