import React from 'react';
import { Link } from 'react-router-dom';
import './Header.css';  // We will create this CSS file for styling
import logo from '../assets/images/logo.png';

function Header() {
  return (
    <header className="header">
      <div className="header-left">
        <Link to="/">
            <img src={logo} alt="Shopee Logo" className="logo" />
        </Link>
        <div>Shopee</div>
      </div>

      <div className="header-center">
        <input type="text"  placeholder="Điện thoại" className="search-input" />
        <button className="search-button">
          <i className="fa fa-search"></i>  {/* Font Awesome search icon */}
        </button>
      </div>

      <div className="header-right">
        <Link to="/cart">
          <i className="fa fa-shopping-cart"></i>  {/* Font Awesome cart icon */}
        </Link>
      </div>
    </header>
  );
}

export default Header;
